html:
{% set sample_collection = frappe.get_doc("Sample Collection", doc.sample) %}
<div>
  {% if letter_head and not no_letterhead -%}
    <div class="letter-head">{{ letter_head }}</div>
    <hr>
  {%- endif %}

  {% if doc.docstatus != 1 or doc.lab_status == "Completed" %}
  <div><h2 class="text-uppercase text-center"><b> </b></h2></div>
  <br/>
  <div class="row section-break">
    <div class="col-xs-6 column-break">
      <div class="row">
        <div class="col-xs-4 text-left">
          <label><b>Patient</b></label>
        </div>
        <div class="col-xs-7 value">
          {{ doc.patient_name or doc.patient }}
        </div>
      </div>

      <div class="row">
        <div class="col-xs-4 text-left">
          <label><b>UID</b></label>
        </div>
        <div class="col-xs-7 value">
          {{ doc.uid or '' }}
        </div>
      </div>

      <div class="row">
        <div class="col-xs-4 text-left">
          <label><b>Age</b></label>
        </div>
        <div class="col-xs-7 value">
          {{ doc.patient_age or '' }}
        </div>
      </div>

      <div class="row">
        <div class="col-xs-4 text-left">
          <label><b>Gender</b></label>
        </div>
        <div class="col-xs-7 value">
          {{ doc.patient_sex or '' }}
        </div>

      </div>
      <div class="row">
  <div class="col-xs-4 text-left">
    <label><b>Report Date</b></label>
  </div>
  <div class="col-xs-7 text-left value">
    {{ doc.result_date or "No result date available." }}
  </div>
</div>
    </div>

    <div class="col-xs-6 column-break">
      <div class="row">
        <div class="col-xs-4 text-left">
          <label><b>Practitioner</b></label>
        </div>
        <div class="col-xs-7 text-left value">
          {{ doc.practitioner_name or doc.referring_practitioner_name or '' }}
        </div>
      </div>


      <div class="row">
  <div class="col-xs-4 text-left">
    <label><b>Order Date</b></label>
  </div>
  <div class="col-xs-7 text-left value">
    {{ doc.creation or "No order date available." }}
  </div>
</div>

      <div class="row">
        <div class="col-xs-4 text-left">
          <label><b>Acknowledge Date</b></label>
        </div>
        <div class="col-xs-7 text-left value">
          {% if sample_collection %}
            {% if sample_collection.observation_sample_collection %}
              <p>{{ sample_collection.observation_sample_collection[0].collection_date_time }}</p>
            {% else %}
              <p>No collection date available.</p>
            {% endif %}
          {% else %}
            <p>No sample collection data available.</p>
          {% endif %}
        </div>
      </div>
      <div class="row">
  <div class="col-xs-4 text-left">
    <label><b>Collection Date</b></label>
  </div>
  <div class="col-xs-7 text-left value">
    {{ sample_collection.creation or "No collection date available." }}
  </div>
</div>


    </div>
  </div>

  <div>
    <hr>
    <h4 class="text-uppercase text-center"><b><u>Department of {{ doc.department }}</u></b></h4>
  </div>

  <h3>Test Results</h3>
  {% if doc.normal_test_items %}
  <table border="1" width="100%" cellspacing="0" cellpadding="5" style="font-size: 12px;">
    <tr>
      <th style="font-size: 12px;">Lab Test Name</th>
      <th style="font-size: 12px;">Result Value</th>
      <th style="font-size: 12px;">Unit</th>
      <th style="font-size: 12px;">Normal Range</th>
      <th style="font-size: 12px;">Remarks</th>
    </tr>
    {% for test in doc.normal_test_items %}
    <tr>
      <td style="font-size: 12px;">{{ test.lab_test_name or '' }}</td>
      <td style="font-size: 12px;">{{ test.result_value or '' }}</td>
      <td style="font-size: 12px;">{{ test.lab_test_uom or '' }}</td>
      <td style="font-size: 12px;">{{ test.normal_range or '' }}</td>
      <td style="font-size: 12px;">{{ test.lab_test_comment or '' }}</td>
    </tr>
    {% endfor %}
  </table>
  {% else %}
  <p>No test results available.</p>
  {% endif %}

  <div class="footer"></div>

  {% endif %}
</div>
<style>
  body {
    font-size: 14px;
    font-family: Arial, sans-serif;
  }
  .letter-head {
    font-size: 16px;
    font-weight: bold;
    text-align: center;
  }
  label, th {
    font-weight: bold;
  }
  table {
    width: 100%;
    border-collapse: collapse;
    margin-top: 10px;
  }
  th, td {
    border: 1px solid black;
    padding: 10px;
    text-align: left;
  }
  th {
    background-color: #f2f2f2;
    font-size: 14px;
  }
</style>
<div style="margin-top: 30px;">
  <hr>

  <div class="row">
    <div class="col-xs-6">
      <p><strong>Prepared By</strong></p>
      <p>{{ doc.prepared_by or '' }}</p>
    </div>

    <div class="col-xs-6 text-right">
      <p><strong>Verified By</strong></p>
      {% if doc.verifier_signature %}
        <img src="{{ doc.verifier_signature }}" alt="Signature" style="width: 100px; height: auto;">
      {% else %}
        <p></p>
      {% endif %}
      <p>{{ doc.verifier_name or '' }}</p>
      <p>{{ doc.verifier_designation or '' }}</p>
    </div>
  </div>

  <br/>

  <div class="row">
    <div class="col-xs-12 text-center">
      <p>------------------------- End of the Report -------------------------</p>
    </div>
  </div>
   <div class="row">
    <div class="col-xs-12 text-left">
      <p>1. The report is only an aid to clinical decision.</p>
      <p>2. Contact Lab in-charge for any clarification.</p>
    </div>
  </div>
<br>
<br>
<br>
<br>
<br>
<br>
<br>
<br>
 <div class="row">
    <div class="col-xs-12 text-left">
      <p><strong>Print Date/Time:</strong> {{ frappe.utils.now_datetime().strftime('%d-%m-%Y %I:%M %p') }}</p>
    </div>
  </div>
</div>

<style>
  .footer {
    margin-top: 20px;
    font-size: 12px;
  }
  .text-right {
    text-align: right;
  }
  .text-left {
    text-align: left;
  }
  .text-center {
    text-align: center;
  }
</style>
